var SQUARE = 0,
  SAWTOOTH = 1,
  SINE = 2,
  NOISE = 3;

function sqr(x) { return x * x }
function cube(x) { return x * x * x }
function sign(x) { return x < 0 ? -1 : 1 }
function log(x, b) { return Math.log(x) / Math.log(b); }
var pow = Math.pow;

function frnd(range) {
  return Math.random() * range;
}

function rndr(from, to) {
  return Math.random() * (to - from) + from;
}

function rnd(max) {
  return Math.floor(Math.random() * (max + 1));
}

// These functions roll up random sounds appropriate to various
// typical game events:

module.exports = {
  pickupCoin: function (p) {
    p.freq = 0.4 + frnd(0.5);
    p.attack = 0;
    p.sustain = frnd(0.1);
    p.decay = 0.1 + frnd(0.4);
    p.punch = 0.3 + frnd(0.3);
    if (rnd(1)) {
      p.arpSpeed = 0.5 + frnd(0.2);
      p.arpMod = 0.2 + frnd(0.4);
    }
    return p;
  },
  laserShoot: function (p) {
    p.shape = rnd(2);
    if(p.shape === SINE && rnd(1))
      p.shape = rnd(1);
    if (rnd(2) === 0) {
      p.freq = 0.3 + frnd(0.6);
      p.freqLimit = frnd(0.1);
      p.freqSlide = -0.35 - frnd(0.3);
    } else {
      p.freq = 0.5 + frnd(0.5);
      p.freqLimit = p.freq - 0.2 - frnd(0.6);
      if (p.freqLimit < 0.2) p.freqLimit = 0.2;
      p.freqSlide = -0.15 - frnd(0.2);
    }
    if (p.shape === SAWTOOTH)
      p.duty = 1;
    if (rnd(1)) {
      p.duty = frnd(0.5);
      p.dutySweep = frnd(0.2);
    } else {
      p.duty = 0.4 + frnd(0.5);
      p.dutySweep = -frnd(0.7);
    }
    p.attack = 0;
    p.sustain = 0.1 + frnd(0.2);
    p.decay = frnd(0.4);
    if (rnd(1))
      p.punch = frnd(0.3);
    if (rnd(2) === 0) {
      p.flangerOffset = frnd(0.2);
      p.flangerSweep = -frnd(0.2);
    }
    //if (rnd(1))
      p.hpf = frnd(0.3);

    return p;
  },
  explosion: function (p) {
    p.shape = NOISE;
    if (rnd(1)) {
      p.freq = sqr(0.1 + frnd(0.4));
      p.freqSlide = -0.1 + frnd(0.4);
    } else {
      p.freq = sqr(0.2 + frnd(0.7));
      p.freqSlide = -0.2 - frnd(0.2);
    }
    if (rnd(4) === 0)
      p.freqSlide = 0;
    if (rnd(2) === 0)
      p.repeatSpeed = 0.3 + frnd(0.5);
    p.attack = 0;
    p.sustain = 0.1 + frnd(0.3);
    p.decay = frnd(0.5);
    if (rnd(1)) {
      p.flangerOffset = -0.3 + frnd(0.9);
      p.flangerSweep = -frnd(0.3);
    }
    p.punch = 0.2 + frnd(0.6);
    if (rnd(1)) {
      p.vibDepth = frnd(0.7);
      p.vibSpeed = frnd(0.6);
    }
    if (rnd(2) === 0) {
      p.arpSpeed = 0.6 + frnd(0.3);
      p.arpMod = 0.8 - frnd(1.6);
    }

    return p;
  },
  powerUp: function (p) {
    if (rnd(1)) {
      p.shape = SAWTOOTH;
      p.duty = 1;
    } else {
      p.duty = frnd(0.6);
    }
    p.freq = 0.2 + frnd(0.3);
    if (rnd(1)) {
      p.freqSlide = 0.1 + frnd(0.4);
      p.repeatSpeed = 0.4 + frnd(0.4);
    } else {
      p.freqSlide = 0.05 + frnd(0.2);
      if (rnd(1)) {
        p.vibDepth = frnd(0.7);
        p.vibSpeed = frnd(0.6);
      }
    }
    p.attack = 0;
    p.sustain = frnd(0.4);
    p.decay = 0.1 + frnd(0.4);

    return p;
  },
  hitHurt: function (p) {
    p.shape = rnd(2);
    if (p.shape === SINE)
      p.shape = NOISE;
    if (p.shape === SQUARE)
      p.duty = frnd(0.6);
    if (p.shape === SAWTOOTH)
      p.duty = 1;
    p.freq = 0.2 + frnd(0.6);
    p.freqSlide = -0.3 - frnd(0.4);
    p.attack = 0;
    p.sustain = frnd(0.1);
    p.decay = 0.1 + frnd(0.2);
    if (rnd(1))
      p.hpf = frnd(0.3);
    return p;
  },
  jump: function (p) {
    p.shape = SQUARE;
    p.duty = frnd(0.6);
    p.freq = 0.3 + frnd(0.3);
    p.freqSlide = 0.1 + frnd(0.2);
    p.attack = 0;
    p.sustain = 0.1 + frnd(0.3);
    p.decay = 0.1 + frnd(0.2);
    if (rnd(1))
      p.hpf = frnd(0.3);
    if (rnd(1))
      p.lpf = 1 - frnd(0.6);
    return p;
  },
  blipSelect: function (p) {
    p.shape = rnd(1);
    if (p.shape === SQUARE)
      p.duty = frnd(0.6);
    else
      p.duty = 1;
    p.freq = 0.2 + frnd(0.4);
    p.attack = 0;
    p.sustain = 0.1 + frnd(0.1);
    p.decay = frnd(0.2);
    p.hpf = 0.1;
    return p;
  },
  tone: function (p) {
    p.shape = SINE;
    p.freq = 0.35173364; // 440 Hz
    p.attack = 0;
    p.sustain = 0.6641; // 1 sec
    p.decay = 0;
    p.punch = 0;
    return p;
  },
  mutate: function (p) {
    if (rnd(1)) p.freq += frnd(0.1) - 0.05;
    if (rnd(1)) p.freqSlide += frnd(0.1) - 0.05;
    if (rnd(1)) p.freqSlideDelta += frnd(0.1) - 0.05;
    if (rnd(1)) p.duty += frnd(0.1) - 0.05;
    if (rnd(1)) p.dutySweep += frnd(0.1) - 0.05;
    if (rnd(1)) p.vibDepth += frnd(0.1) - 0.05;
    if (rnd(1)) p.vibSpeed += frnd(0.1) - 0.05;
    if (rnd(1)) p.p_vib_delay += frnd(0.1) - 0.05;
    if (rnd(1)) p.attack += frnd(0.1) - 0.05;
    if (rnd(1)) p.sustain += frnd(0.1) - 0.05;
    if (rnd(1)) p.decay += frnd(0.1) - 0.05;
    if (rnd(1)) p.punch += frnd(0.1) - 0.05;
    if (rnd(1)) p.lpfResonance += frnd(0.1) - 0.05;
    if (rnd(1)) p.lpf += frnd(0.1) - 0.05;
    if (rnd(1)) p.lpfSweep += frnd(0.1) - 0.05;
    if (rnd(1)) p.hpf += frnd(0.1) - 0.05;
    if (rnd(1)) p.hpfSweep += frnd(0.1) - 0.05;
    if (rnd(1)) p.flangerOffset += frnd(0.1) - 0.05;
    if (rnd(1)) p.flangerSweep += frnd(0.1) - 0.05;
    if (rnd(1)) p.repeatSpeed += frnd(0.1) - 0.05;
    if (rnd(1)) p.arpSpeed += frnd(0.1) - 0.05;
    if (rnd(1)) p.arpMod += frnd(0.1) - 0.05;
    return p
  },
  random: function (p) {
    if (rnd(1))
      p.freq = cube(frnd(2) - 1) + 0.5;
    else
      p.freq = sqr(frnd(1));
    p.freqLimit = 0;
    p.freqSlide = Math.pow(frnd(2) - 1, 5);
    if (p.freq > 0.7 && p.freqSlide > 0.2)
      p.freqSlide = -p.freqSlide;
    if (p.freq < 0.2 && p.freqSlide < -0.05)
      p.freqSlide = -p.freqSlide;
    p.freqSlideDelta = Math.pow(frnd(2) - 1, 3);
    p.duty = frnd(2) - 1;
    p.dutySweep = Math.pow(frnd(2) - 1, 3);
    p.vibDepth = Math.pow(frnd(2) - 1, 3);
    p.vibSpeed = rndr(-1, 1);
    p.attack = cube(rndr(-1, 1));
    p.sustain = sqr(rndr(-1, 1));
    p.decay = rndr(-1, 1);
    p.punch = Math.pow(frnd(0.8), 2);
    if (p.attack + p.sustain + p.decay < 0.2) {
      p.sustain += 0.2 + frnd(0.3);
      p.decay += 0.2 + frnd(0.3);
    }
    p.lpfResonance = rndr(-1, 1);
    p.lpf = 1 - Math.pow(frnd(1), 3);
    p.lpfSweep = Math.pow(frnd(2) - 1, 3);
    if (p.lpf < 0.1 && p.lpfSweep < -0.05)
      p.lpfSweep = -p.lpfSweep;
    p.hpf = Math.pow(frnd(1), 5);
    p.hpfSweep = Math.pow(frnd(2) - 1, 5);
    p.flangerOffset = Math.pow(frnd(2) - 1, 3);
    p.flangerSweep = Math.pow(frnd(2) - 1, 3);
    p.repeatSpeed = frnd(2) - 1;
    p.arpSpeed = frnd(2) - 1;
    p.arpMod = frnd(2) - 1;
    return p;
  },
  reset: function(p) {
    p.shape = 0;

    // Envelope
    p.attack = 0;    // Attack time
    p.sustain = 0.3; // Sustain time
    p.punch = 0;     // Sustain punch
    p.decay = 0.4;   // Decay time

    // Tone
    p.freq = 0.3;    // Start frequency
    p.freqLimit = 0;   // Min frequency cutoff
    p.freqSlide = 0;    // Slide (SIGNED)
    p.freqSlideDelta = 0;   // Delta slide (SIGNED)
    // Vibrato
    p.vibDepth = 0; // Vibrato depth
    p.vibSpeed = 0;    // Vibrato speed

    // Tonal change
    p.arpMod = 0;      // Change amount (SIGNED)
    p.arpSpeed = 0;    // Change speed

    // Square wave duty (proportion of time signal is high vs. low)
    p.duty = 0;         // Square duty
    p.dutySweep = 0;    // Duty sweep (SIGNED)

    // Repeat
    p.repeatSpeed = 0; // Repeat speed

    // Flanger
    p.flangerOffset = 0;   // Flanger offset (SIGNED)
    p.flangerSweep = 0;     // Flanger sweep (SIGNED)

    // Low-pass filter
    p.lpf = 1;     // Low-pass filter cutoff
    p.lpfSweep = 0;     // Low-pass filter cutoff sweep (SIGNED)
    p.lpfResonance = 0;// Low-pass filter resonance
    // High-pass filter
    p.hpf = 0;     // High-pass filter cutoff
    p.hpfSweep = 0;     // High-pass filter cutoff sweep (SIGNED)

    // Sample parameters
    p.vol = 0.5;

    return p;
  }
};

old/demo.js.content:
var PARAMS;
var SOUND;
var vol = 0.25;
var SAMPLE_RATE = 44100;
var SAMPLE_SIZE = 8;

Params.prototype.query = function () {
  var result = "";
  var that = this;
  $.each(this, function (key,value) {
    if (that.hasOwnProperty(key))
      result += "&" + key + "=" + value;
  });
  return result.substring(1);
};

function gen(fx) {
  PARAMS = new Params();
  PARAMS.vol = vol;
  PARAMS.sample_rate = SAMPLE_RATE;
  PARAMS.sample_size = SAMPLE_SIZE;
  PARAMS[fx]();
  $("#wav").text(fx + ".wav");
  updateUi();
  play();
}

function mut() {
  PARAMS.mutate();
  updateUi();
  play();
}

function play(noregen) {
  setTimeout(function () {
    var audio = new Audio();
    if (!noregen) {
      SOUND = new SoundEffect(PARAMS).generate();
      $("#file_size").text(Math.round(SOUND.wav.length / 1024) + "kB");
      $("#num_samples").text(SOUND.header.subChunk2Size /
                             (SOUND.header.bitsPerSample >> 3));
      $("#clipping").text(SOUND.clipping);
    }
    audio.src = SOUND.dataURI;
    $("#wav").attr("href", SOUND.dataURI);
    $("#sfx").attr("href", "sfx.wav?" + PARAMS.query());
    audio.play();
  }, 0);
}

function disenable() {
  var duty = PARAMS.shape == SQUARE || PARAMS.shape == SAWTOOTH;
  $("#duty").slider("option", "disabled", !duty);
  $("#dutySweep").slider("option", "disabled", !duty);
}

function updateUi() {
  $.each(PARAMS, function (param, value) {
    if (param == "shape") {
      $("#shape input:radio[value=" + value + "]").
        prop('checked', true).button("refresh");
    } else if (param == "sample_rate") {
      $("#hz input:radio[value=" + value + "]").
        prop('checked', true).button("refresh");
    } else if (param == "sample_size") {
      $("#bits input:radio[value=" + value + "]").
        prop('checked', true).button("refresh");
    } else {
      var id = "#" + param;
      $(id).slider("value", 1000 * value);
      $(id).each(function(){convert(this, PARAMS[this.id]);});
    }
  });
  disenable();
}


$(function() {
  $("#shape").buttonset();
  $("#hz").buttonset();
  $("#bits").buttonset();
  $("#shape input:radio").change(function (event) {
    PARAMS.shape = parseInt(event.target.value);
    disenable();
    play();
  });
  $("#hz input:radio").change(function (event) {
    SAMPLE_RATE = PARAMS.sample_rate = parseInt(event.target.value);
    play();
  });
  $("#bits input:radio").change(function (event) {
    SAMPLE_SIZE = PARAMS.sample_size = parseInt(event.target.value);
    play();
  });
  $("button").button();
  $(".slider").slider({
    value: 1000,
    min: 0,
    max: 1000,
    slide: function (event, ui) {
      convert(event.target, ui.value / 1000.0);
    },
    change: function(event, ui) {
      if (event.originalEvent) {
        PARAMS[event.target.id] = ui.value / 1000.0;
        convert(event.target, PARAMS[event.target.id]);
        play();
      }
    }
  });
  $(".slider").filter(".signed").
    slider("option", "min", -1000).
    slider("value", 0);
  $('.slider').each(function () {
      var is = this.id;
      if (!$('label[for="' + is + '"]').length)
        $(this).parent().parent().find('th').append($('<label>',
                                                      {for: is}));
    });

  var UNITS = {
    attack:  function (v) { return (v / 44100).toPrecision(4) + ' sec' },
    sustain: function (v) { return (v / 44100).toPrecision(4) + ' sec' },
    punch:   function (v) { return '+' + (v * 100).toPrecision(4) + '%'},
    decay:   function (v) { return (v / 44100).toPrecision(4) + ' sec' },

    freq:  'Hz',
    freqLimit: 'Hz',
    freqSlide:  function (v) {
      return (44100*Math.log(v)/Math.log(0.5)).toPrecision(4) + ' 8va/sec'; },
    freqSlideDelta: function (v) {
      return (v*44100 / Math.pow(2, -44101/44100)).toPrecision(4) +
        ' 8va/sec^2?'; },

    vibSpeed:    function (v) { return v === 0 ? 'OFF' :
                                   (441000/64 * v).toPrecision(4) + ' Hz'},
    vibDepth: function (v) { return v === 0 ? 'OFF' :
                                   '&plusmn; ' + (v*100).toPrecision(4) + '%' },

    arpMod:   function (v) { return ((v === 1) ? 'OFF' :
                                        'x ' + (1/v).toPrecision(4)) },
    arpSpeed: function (v) { return (v === 0 ? 'OFF' :
                                        (v / 44100).toPrecision(4) +' sec') },

    duty:      function (v) { return (100 * v).toPrecision(4) + '%'; },
    dutySweep: function (v) { return (8 * 44100 * v).toPrecision(4) +'%/sec'},

    repeatSpeed: function (v) { return v === 0 ? 'OFF' :
                                   (44100/v).toPrecision(4) + ' Hz' },

    flangerOffset: function (v) { return v === 0 ? 'OFF' :
                                 (1000*v/44100).toPrecision(4) + ' msec' },
    // Not so sure about this:
    flangerSweep:   function (v) { return v === 0 ? 'OFF' :
                 (1000*v).toPrecision(4) + ' msec/sec' },

    lpf:   function (v) {
      return (v === 0.1) ? 'OFF' : Math.round(8 * 44100 * v / (1-v)) + ' Hz'; },
    lpfSweep:  function (v) {  if (v === 1) return 'OFF';
      return Math.pow(v, 44100).toPrecision(4) + ' ^sec'; },
    lpfResonance: function (v) { return (100*(1-v*0.11)).toPrecision(4)+'%';},

    hpf:   function (v) {
      return (v === 0) ? 'OFF' : Math.round(8 * 44100 * v / (1-v)) + ' Hz'; },
    hpfSweep: function (v) {  if (v === 1) return 'OFF';
      return Math.pow(v, 44100).toPrecision(4) + ' ^sec'; },

    vol: function (v) {
      v = 10 * Math.log(v*v) / Math.log(10);
      var sign = v >= 0 ? '+' : '';
      return sign + v.toPrecision(4) + ' dB';
    }
  };

  var CONVERSIONS = {
    attack:  function (v) { return v * v * 100000.0 },
    sustain: function (v) { return v * v * 100000.0 },
    punch:   function (v) { return v },
    decay:   function (v) { return v * v * 100000.0 },

    freq:  function (v) { return 8 * 44100 * (v * v + 0.001) / 100 },
    freqLimit: function (v) { return 8 * 44100 * (v * v + 0.001) / 100 },
    freqSlide:  function (v) { return 1.0 - Math.pow(v, 3.0) * 0.01 },
    freqSlideDelta: function (v) { return -Math.pow(v, 3.0) * 0.000001 },

    vibSpeed:    function (v) { return Math.pow(v, 2.0) * 0.01 },
    vibDepth: function (v) { return v * 0.5 },

    arpMod:   function (v) {
      return v >= 0 ? 1.0 - Math.pow(v, 2) * 0.9 : 1.0 + Math.pow(v, 2) * 10; },
    arpSpeed: function (v) { return (v === 1.0) ? 0 :
                                Math.floor(Math.pow(1.0 - v, 2.0) * 20000 +32)},

    duty:      function (v) { return 0.5 - v * 0.5; },
    dutySweep: function (v) { return -v * 0.00005 },

    repeatSpeed: function (v) { return (v === 0) ? 0 :
                                   Math.floor(Math.pow(1-v, 2) * 20000) + 32 },

    flangerOffset: function (v) { return (v < 0 ? -1 : 1) * Math.pow(v,2)*1020 },
    flangerSweep:   function (v) { return (v < 0 ? -1 : 1) * Math.pow(v,2) },

    lpf:   function (v) { return Math.pow(v, 3) * 0.1 },
    lpfSweep:   function (v) { return 1.0 + v * 0.0001 },
    lpfResonance: function (v) { return 5.0 / (1.0 + Math.pow(v, 2) * 20) }, // * (0.01 + fltw);

    hpf: function (v) { return Math.pow(v, 2) * 0.1 },
    hpfSweep: function (v) { return 1.0 + v * 0.0003 },

    vol: function (v) { return Math.exp(v) - 1; }
  };
  for (var p in CONVERSIONS) {
    var control = $('#' + p)[0];
    control.convert = CONVERSIONS[p];
    control.units = UNITS[p];
  }

  gen("pickupCoin");
});

function convert(control, v) {
  if (control.convert) {
    v = control.convert(v);
    control.convertedValue = v;
    if (typeof control.units === 'function')
      v = control.units(v);
    else
      v = v.toPrecision(4) + ' ' + control.units;
    $('label[for="' + control.id + '"]').html(v);
  }
}

