        return typeof observableProps[name] === "function" ? observableProps[name](params[name]) : void 0;
      });
    };
    activeEffect.observe(resyncProps);
    Trigger(element);
    return element;
  };

}).call(this);

spectrum.content:
(function() {
  module.exports = function(buffer, canvas, displayWidth, displayHeight) {
    var backgroundColor, context, height, markerColor, marksPerSecond, n, numberOfMarks, sampleRate, t, timeDomainColor, width, x, xScale;
    if (displayHeight == null) {
      displayHeight = 200;
    }
    sampleRate = 44100;
    xScale = displayWidth / buffer.length;
    width = displayWidth;
    height = displayHeight;
    timeDomainColor = "#F00";
    backgroundColor = "black";
    markerColor = "white";
    canvas.width = width;
    canvas.height = height;
    context = canvas.getContext('2d');
    context.fillStyle = backgroundColor;
    context.fillRect(0, 0, width, height);
    context.lineWidth = 2;
    context.strokeStyle = timeDomainColor;
    buffer.forEach(function(value, index) {
      var x, y;
      x = index * xScale;
      y = height * (value + 1) / 2;
      if (index === 0) {
        context.beginPath();
        return context.moveTo(x, y);
      } else {
        return context.lineTo(x, y);
      }
    });
    context.stroke();
    context.lineWidth = 1;
    marksPerSecond = 16;
    numberOfMarks = buffer.length / (sampleRate / marksPerSecond);
    n = 0;
    while (n < numberOfMarks) {
      n += 1;
      t = n / marksPerSecond;
      x = t * xScale * sampleRate;
      context.fillStyle = backgroundColor;
      context.fillText("" + t, x + 7, 13);
      context.fillStyle = markerColor;
      context.fillRect(x, 0, 1, height);
      context.fillText("" + t, x + 6, 12);
    }
    return canvas;
  };

}).call(this);

style.content:
module.exports = "* {
  box-sizing: border-box;
}
body,
html {
  height: 100%;
}
body {
  background-color: #000;
  color: #fff;
  font-family: "HelveticaNeue-Light", "Helvetica Neue Light", "Helvetica Neue", Helvetica, Arial, "Lucida Grande", sans-serif;
  font-size: 16px;
  font-weight: 300;
  margin: 0;
}
body > application {
  display: flex;
  flex-direction: column;
  height: 100%;
}
#modal > section.help {
  background-color: rgba(0,0,0,0.875);
  color: #080;
  padding: 2em;
  font-weight: bold;
  font-variant: small-caps;
  font-size: 16px;
}
#modal > section.help a {
  color: #ffa500;
}
button,
a.button {
  color: #eee;
  cursor: pointer;
  border-style: outset;
  border-width: 2px;
  background-color: #777;
  border-top-color: #708090;
  border-left-color: #708090;
  border-bottom-color: #666;
  border-right-color: #666;
  display: inline-block;
  font-family: inherit;
  font-size: inherit;
  font-weight: inherit;
  line-height: 1em;
  padding: 0.5em;
  text-decoration: none;
  text-shadow: 1px 1px #000;
  user-select: none;
}
div.fxz-item > div.actions > button,
div.fxz-item > div.actions > a.button {
  font-size: 12px;
}
h2 {
  font-size: 1em;
  margin: 0;
  text-decoration: underline;
}
application > canvas {
  display: block;
  height: 200px;
  margin: 4px 0;
  width: 100%;
}
section.generators {
  display: flex;
  flex: 0 0 auto;
}
section.generators > button {
  flex: 1;
}
section.controls {
  display: flex;
  flex: 0 0 auto;
}
section.controls > section.group {
  display: flex;
  flex: 1;
  flex-direction: column;
}
section.controls > section.group:first-child {
  flex: 0;
}
section.controls > section.group > h2 {
  font-size: 12px;
}
section.wave-shape > label {
  align-items: center;
  display: flex;
  justify-content: space-between;
  padding: 0 0 4px;
}
section.wave-shape > label > h3 {
  display: inline;
  font-size: 1em;
  font-weight: normal;
  margin: 0;
  margin-right: 4px;
}
section.wave-shape > label > input[type="radio"] {
  display: none;
}
span.checked::before {
  content: "☑";
}
span.unchecked::before {
  content: "☐";
}
input[type="radio"]:checked ~ span.unchecked {
  display: none;
}
input[type="radio"]:not(:checked) ~ span.checked {
  display: none;
}
section.collection {
  display: flex;
  flex: 1;
  flex-direction: column;
}
section.collection > div.items {
  overflow: auto;
}
div.fxz-item {
  display: flex;
  height: 32px;
}
div.fxz-item > canvas {
  flex: 1;
  width: 100px;
}
div.fxz-item > label {
  display: flex;
}
div.fxz-item > label > input {
  border: none;
  background-color: #000;
  color: #fff;
  padding: 4px 6px;
}
div.fxz-item > label > input:focus {
  outline: 2px solid #f00;
}
section.control {
  align-items: center;
  display: flex;
  position: relative;
}
section.control > input[type="range"] {
  width: 100%;
}
section.control > label {
  position: absolute;
  pointer-events: none;
  font-size: 12px;
  font-variant: small-caps;
  right: 3px;
  top: -6px;
  z-index: -1;
}
";

