views/collection-item.content:
(function() {
  var ItemTemplate, Spectrum;

  ItemTemplate = require("../templates/fxz-item");

  Spectrum = require("../spectrum");

  module.exports = function(effect, select, remove, duplicate) {
    var element, self, update, waveformCanvas;
    element = ItemTemplate({
      name: effect.name,
      click: function(e) {
        return select(effect);
      },
      remove: function() {
        self.dispose();
        return remove(self);
      },
      duplicate: function() {
        return duplicate(effect);
      },
      fxzURL: effect.fxzURL,
      fxzFilename: effect.fxzFilename,
      wavURL: effect.wavURL,
      wavFilename: effect.wavFilename
    });
    waveformCanvas = element.querySelector('canvas');
    self = {
      name: effect.name,
      fxzBuffer: function() {
        return effect.fxzBuffer();
      },
      dispose: function() {
        return effect.off("update", update);
      },
      element: element
    };
    update = function() {
      return Spectrum(effect.samples(), waveformCanvas, waveformCanvas.clientWidth, 32);
    };
    effect.on("update", update);
    setTimeout(function() {
      return update();
    });
    return self;
  };

}).call(this);

views/collection.content:
(function() {
  var CollectionTemplate, Exporter, Item, Modal, Observable, UI;

  Observable = require("/lib/jadelet-runtime").Observable;

  UI = require("sys").UI;

  Modal = UI.Modal;

  Item = require("./collection-item");

  CollectionTemplate = require("../templates/collection");

  Exporter = require('../export');

  module.exports = function() {
    var duplicate, element, items, selectAndPlay, self;
    items = Observable([]);
    selectAndPlay = function(effect) {
      self.activeItem(effect);
      return effect.play();
    };
    duplicate = function(effect) {
      return self.add(effect.duplicate());
    };
    self = {
      activeItem: Observable(null),
      add: function(effect) {
        items.push(Item(effect, selectAndPlay, items.remove, duplicate));
        return self.activeItem(effect);
      },
      clear: function() {
        items.forEach(function(item) {
          return item.dispose();
        });
        return items([]);
      },
      itemElements: function() {
        return items.map(function(item) {
          return item.element;
        });
      },
      element: null,
      displayHelp: function() {
        var helpSection;
        helpSection = require("../templates/help")();
        return Modal.show(helpSection);
      },
      download: function(blob, name) {
        var a, url;
        url = window.URL.createObjectURL(blob);
        a = document.createElement("a");
        a.href = url;
        a.download = name;
        a.click();
        return window.URL.revokeObjectURL(url);
      },
      downloadJS: function() {
        return self.download(self.JSBlob(), "fxz.js");
      },
      JSBlob: function() {
        return new Blob([Exporter(self.FXXBuffer())], {
          type: "application/javascript"
        });
      },
      downloadFXX: function() {
        return self.download(self.FXXBlob(), "sounds.fxx");
      },
      FXXBuffer: function() {
        var arrayBuffer, dataView, entries, uint8Array;
        entries = items().length;
        arrayBuffer = new ArrayBuffer(8 + 116 * entries);
        uint8Array = new Uint8Array(arrayBuffer);
        dataView = new DataView(arrayBuffer);
        [0x66, 0x78, 0x78, 0x01].forEach(function(n, i) {
          return dataView.setUint8(i, n);
        });
        dataView.setUint32(4, entries, true);
        items.forEach(function(item, i) {
          var encodedName, nameBuffer;
          encodedName = new TextEncoder("utf-8").encode(item.name());
          nameBuffer = new Uint8Array(16);
          nameBuffer.forEach(function(_, i) {
            return nameBuffer[i] = encodedName[i] || 0;
          });
          uint8Array.set(nameBuffer, 8 + 116 * i);
          return uint8Array.set(new Uint8Array(item.fxzBuffer()), 24 + 116 * i);
        });
        return arrayBuffer;
      },
      FXXBlob: function() {
        return new Blob([self.FXXBuffer()], {
          type: "application/fxx"
        });
      }
    };
    element = CollectionTemplate(self);
    self.element = element;
    return self;
  };

}).call(this);

fxz.js.content:
// Synthesize an AudioBuffer from the param data
module.exports = (function() {
  // Sound generation parameters are on [0,1] unless noted SIGNED & thus on [-1,1]
  function Params() {
    // Wave shape
    this.shape = 0;

    // Envelope
    this.attack = 0;    // Attack time
    this.sustain = 0.3; // Sustain time
    this.punch = 0;     // Sustain punch
    this.decay = 0.4;   // Decay time

    // Tone
    this.freq = 0.3;    // Start frequency
    this.freqLimit = 0;   // Min frequency cutoff
    this.freqSlide = 0;    // Slide (SIGNED)
    this.freqSlideDelta = 0;   // Delta slide (SIGNED)
    // Vibrato
    this.vibDepth = 0; // Vibrato depth
    this.vibSpeed = 0;    // Vibrato speed

    // Tonal change
    this.arpMod = 0;      // Change amount (SIGNED)
    this.arpSpeed = 0;    // Change speed

    // Square wave duty (proportion of time signal is high vs. low)
    this.duty = 0;         // Square duty
    this.dutySweep = 0;    // Duty sweep (SIGNED)

    // Repeat
    this.repeatSpeed = 0; // Repeat speed

    // Flanger
    this.flangerOffset = 0;   // Flanger offset (SIGNED)
    this.flangerSweep = 0;     // Flanger sweep (SIGNED)

    // Low-pass filter
    this.lpf = 1;     // Low-pass filter cutoff
    this.lpfSweep = 0;     // Low-pass filter cutoff sweep (SIGNED)
    this.lpfResonance = 0;// Low-pass filter resonance
    // High-pass filter
    this.hpf = 0;     // High-pass filter cutoff
    this.hpfSweep = 0;     // High-pass filter cutoff sweep (SIGNED)

    // Sample parameters
    this.vol = 0.5;
  }

  function FXZ(ps, audioContext) {
    // Handle binary format
    if (ps instanceof ArrayBuffer) {
      ps = Serializer.deserialize(ps, new Params());
    }

    var m = Math;
    var floor = m.floor,
      pow = m.pow,
      abs = m.abs,
      random = m.random;

    var SQUARE = 0,
      SAWTOOTH = 1,
      SINE = 2,
      NOISE = 3,
      OVERSAMPLING = 8,
      sampleRate = 44100;

    var i,
      elapsedSinceRepeat,
      period,
      periodMax,
      enableFrequencyCutoff,
      periodMult,
      periodMultSlide,
      dutyCycle,
      dutyCycleSlide,
      arpeggioMultiplier,
      arpeggioTime;

    function initForRepeat() {
      elapsedSinceRepeat = 0;

      period = 100 / (ps.freq * ps.freq + 0.001);
      periodMax = 100 / (ps.freqLimit * ps.freqLimit + 0.001);
      enableFrequencyCutoff = (ps.freqLimit > 0);
      periodMult = 1 - pow(ps.freqSlide, 3) * 0.01;
      periodMultSlide = -pow(ps.freqSlideDelta, 3) * 0.000001;

      dutyCycle = 0.5 - ps.duty * 0.5;
      dutyCycleSlide = -ps.dutySweep * 0.00005;

