    var fltw = pow(ps.lpf, 3) * 0.1;
    var enableLowPassFilter = (ps.lpf != 1);
    var fltw_d = 1 + ps.lpfSweep * 0.0001;
    var fltdmp = 5 / (1 + pow(ps.lpfResonance, 2) * 20) * (0.01 + fltw);
    if (fltdmp > 0.8)
