    var vibratoSpeed = pow(ps.vibSpeed, 2) * 0.01;
    var vibratoAmplitude = ps.vibDepth * 0.5;

    // Envelope
    var envelopeLength = [
      floor(ps.attack * ps.attack * 100000),
