      if (iphase > 1023)
        iphase = 1023;

      if (flthp_d !== 0) {
        flthp *= flthp_d;
        if (flthp < 0.00001)
          flthp = 0.00001;
        if (flthp > 0.1)
          flthp = 0.1;
      }

      // 8x oversampling
      var sample = 0;
      for (var si = 0; si < OVERSAMPLING; ++si) {
        var sub_sample = 0;
        phase++;
        if (phase >= iperiod) {
          phase %= iperiod;
          if (waveShape === NOISE)
            for(var i = 0; i < 32; ++i)
              noise_buffer[i] = random() * 2 - 1;
        }

