        var fp = phase / iperiod;
        if (waveShape === SQUARE) {
          if (fp < dutyCycle)
            sub_sample=0.5;
          else
            sub_sample=-0.5;
        } else if (waveShape === SAWTOOTH) {
          if (fp < dutyCycle)
            sub_sample = -1 + 2 * fp/dutyCycle;
          else
            sub_sample = 1 - 2 * (fp-dutyCycle)/(1-dutyCycle);
        } else if (waveShape === SINE) {
          sub_sample = m.sin(fp * 2 * m.PI);
        } else if (waveShape === NOISE) {
