(function() {
  var VERSION;

  VERSION = 1;

  module.exports = {
    serialize: function(params) {
      var buffer, dataView;
      buffer = new ArrayBuffer(100);
      dataView = new DataView(buffer);
      Object.keys(params).forEach(function(key, i) {
        var param;
        param = params[key];
        if (i === 0) {
          "fxz".split("").forEach(function(s, n) {
            return dataView.setUint8(n, s.charCodeAt(0));
          });
          dataView.setUint8(3, VERSION);
          return dataView.setUint8(4, param);
        } else {
          return dataView.setFloat32((i + 1) * 4, param, true);
        }
      });
      return buffer;
    },
    deserialize: function(buffer, params) {
      var dataView;
      dataView = new DataView(buffer);
      Object.keys(params).forEach(function(key, i) {
        var version;
        if (i === 0) {
          "fxz".split("").forEach(function(s, n) {
            var charCode;
            charCode = s.charCodeAt(0);
            if (charCode !== dataView.getUint8(n)) {
              throw new Error("Unknown file format: expected '" + s + "' (" + charCode + ") at byte " + n);
            }
          });
          version = dataView.getUint8(3);
          if (version !== VERSION) {
            throw new Error("Unknown version '" + version + "': expected 1");
          }
          return params[key] = dataView.getUint8(4);
        } else {
          return params[key] = dataView.getFloat32((i + 1) * 4, true);
        }
      });
      return params;
    }
  };

}).call(this);

