(function() {
  var equalEnough, rand;

  global.FXZ = require("../fxz");

  console.log(FXZ);

  equalEnough = function(a, b, precision) {
    if (precision == null) {
      precision = 5;
    }
    console.log("equalEnough", a, b);
    return assert.equal(a.toFixed(precision), b.toFixed(precision));
  };

  rand = Math.random;

  describe("FXZ", function() {
    it("should expose Params", function() {
      return assert(FXZ.Params);
    });
    it("should expose Serializer", function() {
      return assert(FXZ.Serializer);
    });
    return describe("Serialization", function() {
      return it("should serialize and deserialize", function() {
        var Params, Serializer, buffer, deserialize, p1, p2, serialize;
        Params = FXZ.Params, Serializer = FXZ.Serializer;
        serialize = Serializer.serialize, deserialize = Serializer.deserialize;
        p1 = new Params;
        Object.keys(p1).forEach(function(key, i) {
          if (i > 0) {
            return p1[key] = rand();
          }
        });
        buffer = serialize(p1);
        assert.equal(buffer.byteLength, 100);
        p2 = deserialize(buffer, new Params);
        return Object.keys(p1).forEach(function(key, i) {
          if (i > 0) {
            return equalEnough(p1[key], p2[key]);
          }
        });
      });
    });
  });

}).call(this);

