(function() {
  Blob.prototype.readAsText = function() {
    var file;
    file = this;
    return new Promise(function(resolve, reject) {
      var reader;
      reader = new FileReader;
      reader.onload = function() {
        return resolve(reader.result);
      };
      reader.onerror = reject;
      return reader.readAsText(file);
    });
  };

  Blob.prototype.getURL = function() {
    return Promise.resolve(URL.createObjectURL(this));
  };

  Blob.prototype.readAsJSON = function() {
    return this.readAsText().then(JSON.parse);
  };

  Blob.prototype.readAsDataURL = function() {
    var file;
    file = this;
    return new Promise(function(resolve, reject) {
      var reader;
      reader = new FileReader;
      reader.onload = function() {
        return resolve(reader.result);
      };
      reader.onerror = reject;
      return reader.readAsDataURL(file);
    });
  };

  Image.fromBlob = function(blob) {
    return blob.getURL().then(function(url) {
      return new Promise(function(resolve, reject) {
        var img;
        img = new Image;
        img.onload = function() {
          return resolve(img);
        };
        img.onerror = reject;
        return img.src = url;
      });
    });
  };

}).call(this);

lib/file-io.content:
(function() {
  var Modal, Observable, UI, _ref;

  _ref = UI = require("ui"), Modal = _ref.Modal, Observable = _ref.Observable;

  module.exports = function(system) {
    return function(self) {
      var confirmUnsaved, currentPath, saved;
      if (self == null) {
        self = {};
      }
      currentPath = Observable("");
      saved = Observable(true);
      confirmUnsaved = function() {
        if (saved()) {
          return Promise.resolve();
        }
        return new Promise(function(resolve, reject) {
          return Modal.confirm("You will lose unsaved progress, continue?").then(function(result) {
            if (result) {
              return resolve();
            } else {
              return reject();
            }
          });
        });
      };
      Object.assign(self, {
        currentPath: currentPath,
        saved: saved,
        "new": function() {
          if (saved()) {
            currentPath("");
            return self.newFile();
          } else {
            return confirmUnsaved().then(function() {
              saved(true);
              return self.newFile();
            });
          }
        },
        open: function() {
          return confirmUnsaved().then(function() {
            return Modal.prompt("File Path", currentPath()).then(function(newPath) {
              if (newPath) {
                return currentPath(newPath);
              } else {
                throw new Error("No path given");
              }
            }).then(function(path) {
              return system.readFile(path, true).then(function(file) {
                return self.loadFile(file, path);
              });
            });
          });
        },
        save: function() {
          if (currentPath()) {
            return self.saveData().then(function(blob) {
              return system.writeFile(currentPath(), blob, true);
            }).then(function() {
              saved(true);
              return currentPath();
            });
          } else {
            return self.saveAs();
          }
        },
        saveAs: function() {
          return Modal.prompt("File Path", currentPath()).then(function(path) {
            if (path) {
              currentPath(path);
              return self.save();
            }
          });
        }
      });
      return self;
    };
  };

}).call(this);

