}
window > resize.h {
  height: 4px;
  width: 100%;
}
window > resize.v {
  height: 100%;
  width: 4px;
}
window > resize.w {
  left: -4px;
}
window > resize.e {
  right: -4px;
}
window > resize.n {
  top: -4px;
}
window > resize.s {
  bottom: -4px;
}
window > resize.n.w {
  cursor: nw-resize;
}
window > resize.n.e {
  cursor: ne-resize;
}
window > resize.s.e {
  cursor: se-resize;
}
window > resize.s.w {
  cursor: sw-resize;
}
window > resize.n.v {
  border-bottom: 1px solid #000;
  height: 23px;
}
window > resize.s.v {
  border-top: 1px solid #000;
  height: 23px;
}
window > resize.e.h {
  border-left: 1px solid #000;
  width: 22px;
}
window > resize.w.h {
  border-right: 1px solid #000;
  width: 22px;
}
window.minimized > header {
  border-bottom: none;
}
window.minimized > header > control {
  display: none;
}
window.minimized > header > control.minimize {
  border-right: none;
  display: inline-block;
}
window.minimized > header > control.minimize::before {
  content: "+";
}
window.minimized > menu,
window.minimized > resize,
window.minimized > viewport {
  display: none;
}
window.maximized {
  border: none;
  border-radius: 0;
  height: 100%;
  width: 100%;
}
window.maximized > resize {
  display: none;
}
window.maximized > header > control {
  display: none;
}
window.maximized > header > control.restore,
window.maximized > header > control.close {
  display: inline-block;
}
frame-guard {
  display: block;
  height: 100%;
  pointer-events: none;
  position: absolute;
  width: 100%;
  z-index: 100000;
}
frame-guard.active {
  pointer-events: auto;
}
";

