(function() {
  var parse;

  parse = require("../lib/indent-parse");

  describe("Menu Parser", function() {
    it("should parse menus into lists", function() {
      var data, results;
      data = "File";
      results = parse(data);
      return assert.deepEqual(["File"], results);
    });
    it("should parse empty", function() {
      var data;
      data = "";
      return assert.deepEqual([], parse(data));
    });
    it("should deal with nesting ok", function() {
      var data, results;
      data = "File
  Open
  Save
Edit
  Copy
  Paste
Help";
      results = parse(data);
      return assert.deepEqual([["File", ["Open", "Save"]], ["Edit", ["Copy", "Paste"]], "Help"], results);
    });
    it("should parse big ol' menus", function() {
      var results;
      results = parse("File
  New
  Open
  Save
  Save As
Edit
  Undo
  Redo
  -
  Cut
  Copy
  Paste
  Delete
  -
  Find
  Find Next
  Replace
  Go To
  -
  Select All
  Time/Date
Format
  Word Wrap
  Font...
View
  Status Bar
Help
  View Help
  -
  About Notepad");
      return assert.deepEqual([["File", ["New", "Open", "Save", "Save As"]], ["Edit", ["Undo", "Redo", "-", "Cut", "Copy", "Paste", "Delete", "-", "Find", "Find Next", "Replace", "Go To", "-", "Select All", "Time/Date"]], ["Format", ["Word Wrap", "Font..."]], ["View", ["Status Bar"]], ["Help", ["View Help", "-", "About Notepad"]]], results);
    });
    return it("should parse hella nested menus", function() {
      var results;
