(function() {
  var MenuView, Observable;

  MenuView = require("../views/menu");

  Observable = require("observable");

  describe("Menu", function() {
    it("should work with plain ol' items", function() {
      var menu;
      menu = MenuView({
        items: ["Cool", "Rad"],
        contextRoot: {
          activeItem: Observable(null),
          handlers: {}
        }
      });
      return assert.equal(menu.items().length, 2);
    });
    return it("should allow observable items", function() {
      var items, menu;
      items = Observable(["Cool", ["Rad", ["2rad", "2Furious"]]]);
      menu = MenuView({
        items: items,
        contextRoot: {
          activeItem: Observable(null),
          handlers: {}
        }
      });
