(function() {
  var Bindable, equal, ok, test;

  test = it;

  ok = assert;

  equal = assert.equal;

  Bindable = require("../README");

  describe("Bindable", function() {
    test("#bind and #trigger", function() {
      var o;
      o = Bindable();
      o.on("test", function() {
        return ok(true);
      });
      return o.trigger("test");
    });
    test("Multiple bindings", function() {
      var o;
      o = Bindable();
      o.on("test", function() {
        return ok(true);
      });
      o.on("test", function() {
        return ok(true);
      });
      return o.trigger("test");
    });
    test("#trigger arguments", function() {
      var o, param1, param2;
      o = Bindable();
      param1 = "the message";
      param2 = 3;
      o.on("test", function(p1, p2) {
        equal(p1, param1);
        return equal(p2, param2);
      });
      return o.trigger("test", param1, param2);
    });
    test("#unbind", function() {
      var callback, o;
      o = Bindable();
      callback = function() {
        return ok(false);
      };
      o.on("test", callback);
      o.off("test", callback);
      o.trigger("test");
      o.on("test", callback);
      o.off("test");
      return o.trigger("test");
    });
    test("#trigger namespace", function() {
      var o;
      o = Bindable();
      o.on("test.TestNamespace", function() {
        return ok(true);
      });
      o.trigger("test");
      o.off(".TestNamespace");
      return o.trigger("test");
    });
    return test("#unbind namespaced", function() {
      var o;
      o = Bindable();
      o.on("test.TestNamespace", function() {
        return ok(true);
      });
      o.trigger("test");
      o.off(".TestNamespace", function() {});
      return o.trigger("test");
    });
  });

}).call(this);
